import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o as s,c as o,d as c,a as e,e as a,w as l,b as t,f as d}from"./app-2a2d189a.js";const _="/assets/figure5-1-03b663d9.png",h="/assets/figure5-2-1f4937cc.png",u={},p=e("h1",{id:"e-第5章git团队协作机制",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#e-第5章git团队协作机制","aria-hidden":"true"},"#"),t(" E-第5章Git团队协作机制")],-1),f=e("p",null,"E-第5章Git团队协作机制",-1),m=e("div",{class:"hint-container info"},[e("p",{class:"hint-container-title"},"说明"),e("p",null,"主要是各种搜索找的学习；")],-1),g={class:"table-of-contents"},G=d('<p>@<a href="%E7%9B%AE%E5%BD%95">TOC</a></p><h2 id="概述框架" tabindex="-1"><a class="header-anchor" href="#概述框架" aria-hidden="true">#</a> 概述框架</h2><h2 id="e-第5章git团队协作机制-1" tabindex="-1"><a class="header-anchor" href="#e-第5章git团队协作机制-1" aria-hidden="true">#</a> E-第5章Git团队协作机制</h2><ul><li><p><mark>Git 团队协作模式</mark></p></li><li><p>此前讲解了诸多内容，接下来探讨在 Git 中如何进行团队协作。Git 是分布式版本控制工具，在个人 PC 端可进行分支和版本控制。然而，在公司团队协作中，需使用代码托管中心。</p></li><li><p>假设公司有程序员小 A 和小 B，各自有自己的版本（V1、V2、V3）。若要实现团队协作，需将代码推送到远程服务器，以便另一方获取并修改。</p></li></ul><h2 id="_5-1-团队内协作" tabindex="-1"><a class="header-anchor" href="#_5-1-团队内协作" aria-hidden="true">#</a> 5.1 团队内协作</h2><ul><li><p>下面讲解 Git 中团队协作的机制，分为团队内协作和跨团队协作两种。</p></li><li><p>先看团队内协作。以岳不群开发华山剑法为例，岳不群作为华山派掌门人，使用 Git 初始化本地库，并在其中编写华山剑法架构。完成后，他想让大弟子令狐冲学习并完善细节，于是使用“push”命令将代码推送到代码托管中心（如 GitHub 等），形成远程库。<br><img src="'+_+'" alt="figure5-1" loading="lazy"></p></li><li><p>令狐冲随后可将远程库克隆到自己本地库，进行修改（如添加两招）。修改后，若要让岳不群查看，需将本地库再次“push”到远程库，前提是岳不群将令狐冲加入团队，赋予其推送权限。岳不群则可使用“pull”命令拉取令狐冲修改后的代码，更新本地库，实现本地库、远程库和令狐冲本地库的同步。</p></li></ul><h2 id="_5-2-跨团队协作" tabindex="-1"><a class="header-anchor" href="#_5-2-跨团队协作" aria-hidden="true">#</a> 5.2 跨团队协作</h2><ul><li><p>再看跨团队协作。岳不群和令狐冲的华山剑法远程库不够完善，令狐冲请好友东方不败帮忙修改为辟邪剑谱。因东方不败是日月神教 CEO，不会加入华山派团队，所以他先从代码托管中心“fork”岳不群的远程库作为分支复制到自己的远程库，再克隆到本地库进行修改，创建多个版本（V1、V2、V3 等）。<br><img src="'+h+'" alt="figure5-2" loading="lazy"></p></li><li><p>东方不败完成辟邪剑谱开发后，将本地库“push”到自己的远程库，然后向岳不群发送“pull request”（拉取请求）。岳不群收到请求后先审核，若代码优秀则进行合并，将东方不败的代码拉取到自己本地库。此后，岳不群和同团队的令狐冲均可使用新的代码。<br> 通过上述武侠故事，讲解了 Git 中的团队内协作和跨团队协作两种模式。</p></li><li><p>后续还要了解fork</p></li></ul>',8);function b(E,V){const i=n("router-link");return s(),o("div",null,[p,f,c(" more "),m,e("nav",g,[e("ul",null,[e("li",null,[a(i,{to:"#概述框架"},{default:l(()=>[t("概述框架")]),_:1})]),e("li",null,[a(i,{to:"#e-第5章git团队协作机制-1"},{default:l(()=>[t("E-第5章Git团队协作机制")]),_:1})]),e("li",null,[a(i,{to:"#_5-1-团队内协作"},{default:l(()=>[t("5.1 团队内协作")]),_:1})]),e("li",null,[a(i,{to:"#_5-2-跨团队协作"},{default:l(()=>[t("5.2 跨团队协作")]),_:1})])])]),G])}const B=r(u,[["render",b],["__file","E-第5章Git团队协作机制.html.vue"]]);export{B as default};
